export default {
  name: 'basetable',
  data: function data() {
    return {
      url: './vuetable.json',
      tableData: [],
      cur_page: 1,
      multipleSelection: [],
      select_cate: '',
      select_word: '',
      del_list: [],
      is_search: false,
      editVisible: false,
      delVisible: false,
      form: {
        name: '',
        date: '',
        address: ''
      },
      idx: -1
    };
  },
  created: function created() {
    this.getData();
  },
  computed: {
    data: function data() {
      var _this = this;

      return this.tableData.filter(function (d) {
        var is_del = false;

        for (var i = 0; i < _this.del_list.length; i++) {
          if (d.name === _this.del_list[i].name) {
            is_del = true;
            break;
          }
        }

        if (!is_del) {
          if (d.address.indexOf(_this.select_cate) > -1 && (d.name.indexOf(_this.select_word) > -1 || d.address.indexOf(_this.select_word) > -1)) {
            return d;
          }
        }
      });
    }
  },
  methods: {
    // 分页导航
    handleCurrentChange: function handleCurrentChange(val) {
      this.cur_page = val;
      this.getData();
    },
    // 获取 easy-mock 的模拟数据
    getData: function getData() {
      var _this2 = this;

      // 开发环境使用 easy-mock 数据，正式环境使用 json 文件
      if (process.env.NODE_ENV === 'development') {
        this.url = '/ms/table/list';
      }

      ;
      this.$axios.post(this.url, {
        page: this.cur_page
      }).then(function (res) {
        _this2.tableData = res.data.list;
      });
    },
    search: function search() {
      this.is_search = true;
    },
    formatter: function formatter(row, column) {
      return row.address;
    },
    filterTag: function filterTag(value, row) {
      return row.tag === value;
    },
    handleEdit: function handleEdit(index, row) {
      this.idx = index;
      var item = this.tableData[index];
      this.form = {
        name: item.name,
        date: item.date,
        address: item.address
      };
      this.editVisible = true;
    },
    handleDelete: function handleDelete(index, row) {
      this.idx = index;
      this.delVisible = true;
    },
    delAll: function delAll() {
      var length = this.multipleSelection.length;
      var str = '';
      this.del_list = this.del_list.concat(this.multipleSelection);

      for (var i = 0; i < length; i++) {
        str += this.multipleSelection[i].name + ' ';
      }

      this.$message.error('删除了' + str);
      this.multipleSelection = [];
    },
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    // 保存编辑
    saveEdit: function saveEdit() {
      this.$set(this.tableData, this.idx, this.form);
      this.editVisible = false;
      this.$message.success("\u4FEE\u6539\u7B2C ".concat(this.idx + 1, " \u884C\u6210\u529F"));
    },
    // 确定删除
    deleteRow: function deleteRow() {
      this.tableData.splice(this.idx, 1);
      this.$message.success('删除成功');
      this.delVisible = false;
    }
  }
};
