import Schart from 'vue-schart';
import bus from "../common/bus";
export default {
  name: 'dashboard',
  data: function data() {
    return {
      name: localStorage.getItem('ms_username'),
      todoList: [{
        title: '今天要修复100个bug',
        status: false
      }],
      data: [],
      data1: [],
      options: {
        title: 'Failed case over past seven days',
        showValue: false,
        fillColor: 'red',
        bottomPadding: 30,
        topPadding: 30
      },
      options2: {
        title: 'Past seven days suite',
        fillColor: '#FC6FA1',
        axisColor: '#008ACD',
        contentColor: '#EEEEEE',
        bgColor: '#F5F8FD',
        bottomPadding: 30,
        topPadding: 30
      },
      url: ''
    };
  },
  components: {
    Schart: Schart
  },
  computed: {
    role: function role() {
      return this.name === 'admin' ? '超级管理员' : '普通用户';
    }
  },
  created: function created() {
    this.handleListener();
    this.getData();
  },
  activated: function activated() {
    this.handleListener();
  },
  deactivated: function deactivated() {
    window.removeEventListener('resize', this.renderChart);
    bus.$off('collapse', this.handleBus);
  },
  methods: {
    handleListener: function handleListener() {
      bus.$on('collapse', this.handleBus); // 调用renderChart方法对图表进行重新渲染

      window.addEventListener('resize', this.renderChart);
    },
    handleBus: function handleBus(msg) {
      var _this = this;

      setTimeout(function () {
        _this.renderChart();
      }, 300);
    },
    renderChart: function renderChart() {
      this.$refs.bar.renderChart();
      this.$refs.line.renderChart();
    },
    getData: function getData() {
      var _this2 = this;

      this.url = "/index.php/Welcome/simplechart";
      this.$axios.post(this.url).then(function (res) {
        var temp = [];
        var temp_suite = [];
        res.data.list.forEach(function (currentValue) {
          var timeObj = new Date(parseInt(currentValue['time']) * 1000);
          var year = timeObj.getFullYear();
          var mouth = timeObj.getMonth() + 1;
          var day = timeObj.getDate();
          var temp_time = year + '/' + mouth + '/' + day;
          temp.push({
            'value': currentValue['failnum'],
            'name': temp_time
          });
          temp_suite.push({
            'value': currentValue['totalnum'],
            'name': temp_time
          });
        });
        _this2.data = temp;
        _this2.data1 = temp_suite;
      });
    }
  }
};
