    methods: {
        submitForm(formName) {
            this.$refs[formName].validate((valid) => {
                if (valid) {
                    this.url = "/index.php/Welcome/check_userlist"
                    this.$axios.get("/index.php/Welcome/check_userlist", this.qs.stringify({'user': this.username, 'pass': this.password}),
