                        const status = res.status
                        if (status === 200) {
                            localStorage.setItem('ms_username',this.ruleForm.username);
                            this.$router.push('/');
                            return true
                        } else {
                            console.log('error submit!!');
                            return false
                        }
                    }).catch(function (error) {
                        console.log(error);
                    })
                } else {
                    console.log('error submit!!');
